sys_variable_value.value:
(function paginate(variables, pageResponse) {
    // Este script determina quando recuperar a próxima página de registros de glacier do web service.

    //Calcular quantos registros restam para recuperar e então determinar o número do último glacier
    var totalRecs = parseInt(variables.glaciers_per_page) * parseInt(variables.num_pages);
    var lastRec = parseInt(variables.start_with) + totalRecs;

    //Se o valor de glacier_number for menor que o último registro, carregar a próxima página. 
    //Caso contrário, passar a variável getNextPage para false
    if(parseInt(variables.glaciers_number) < lastRec){
        variables.getNextPage = true;
    }else{
        variables.getNextPage = false;
    }
})(variables, pageResponse);

